import React, { useState } from 'react';
import { submitContact } from './api';


function App() {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [status, setStatus] = useState('idle');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const validate = () => {
    if (!form.name.trim() || !form.email.trim() || !form.message.trim()) {
      setError('All fields are required.');
      return false;
    }
    // Simple email regex
    if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(form.email)) {
      setError('Please enter a valid email.');
      return false;
    }
    setError('');
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    setStatus('loading');
    try {
      const res = await submitContact(form);
      if (res.success) {
        setStatus('success');
        setForm({ name: '', email: '', message: '' });
      } else {
        setStatus('error');
        setError(res.error || 'Submission failed.');
      }
    } catch {
      setStatus('error');
      setError('Submission failed.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      <header className="bg-white shadow p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-blue-700">HealthTech Portfolio</h1>
        <a href="/docs/capability-statement-placeholder.pdf" download className="text-blue-600 underline">Download Capability Statement</a>
      </header>
      <main className="p-8">
        <section className="mb-8">
          <h2 className="text-xl font-semibold mb-2">Welcome</h2>
          <p>We are a health technology company specialising in data-driven clinical solutions, including machine learning, NLP, and decision support systems.</p>
        </section>
        <section className="mb-8">
          <h2 className="text-xl font-semibold mb-2">Contact Us</h2>
          <form className="space-y-4 max-w-md" onSubmit={handleSubmit}>
            <input
              className="w-full border p-2"
              type="text"
              name="name"
              placeholder="Name"
              value={form.name}
              onChange={handleChange}
              required
            />
            <input
              className="w-full border p-2"
              type="email"
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              required
            />
            <textarea
              className="w-full border p-2"
              name="message"
              placeholder="Message"
              value={form.message}
              onChange={handleChange}
              required
            />
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded disabled:opacity-50"
              type="submit"
              disabled={status === 'loading'}
            >
              {status === 'loading' ? 'Sending...' : 'Send'}
            </button>
            {error && <div className="text-red-600 text-sm">{error}</div>}
            {status === 'success' && <div className="text-green-600 text-sm">Thank you! We'll be in touch.</div>}
          </form>
        </section>
        <section>
          <h2 className="text-xl font-semibold mb-2">Latest Health/Tech News</h2>
          <div className="bg-white p-4 rounded shadow">
            {/* News feed will be implemented here */}
            <p>News feed coming soon...</p>
          </div>
        </section>
      </main>
    </div>
  );
}

export default App;
